import React, { useState, useEffect } from 'react'
import styles from '../styles/Body.scss'
import axios from 'axios'
import ChampContainer from './champContainer'
export const BodyContainer = () => {

    const [state, setState] = useState('')
    const [champions, setchampions] = useState([])
    const [NewChampions, setNewChampions] = useState([])

    useEffect(async() => {
        const response = await axios({
            method: 'get',
            url: 'http://ddragon.leagueoflegends.com/cdn/10.21.1/data/en_US/champion.json',
          });

          const array = []
          for(const property in response.data.data){
              array.push(response.data.data[property])
          }

          setchampions(array)
          setNewChampionsList(array)

        
    }, [])

    const handleChange =(e)=>{
        console.log("Esta Cambiando",e.target.value)
        setState(e.target.value)
        const setNewChampionsList=champions.filter(items=>{
            console.log(item.id)
            if(item.id.toLowerCase.includes(e.target.value.toLowerCase()))
            return item
        })
        console.log("Champ ")
    }

    return (
        <div className={styles.Main}>
            <div className={styles.SearchContainer}>
            <input value={state} name="champion" onChange={handleChange} type="text" placeholder="Campeon"/>
            </div>
            <div className={styles.ChampContainer}>
            {
                    champions.map((item) => {
                        return <ChampContainer key={item.id} data={item} />
                    })
                }
               
            </div>
        </div>
    )
}
